import io from 'socket.io-client';

const chatInit = () => {
  const socket = io.connect('http://localhost:8001');

  socket.on('connection', socket => {
    console.log('connection client');
  })


  const getAllUsers = cb => {
    console.log(cb);

    socket.emit('allUsers', cb);
  };

  return {

    getAllUsers
  };
};

export default chatInit;
