  onMessage = e => {
    this.setState({
      input: e.target.value
    });
  };

  render() {
    const { history } = this.props;
    const { message } = this.state;

    return (
      <ChatWrapper>
        <h1>dialog</h1>
        <p onClick={() => history.push('/')}>back</p>

