import io from 'socket.io-client';

const chatInit = () => {
  const socket = io.connect('http://localhost:8001');

  const getAllUsers = cb => {
    socket.emit('allUsers', cb);
  };

  const register = (name, cb) => {
    socket.emit('register', name, cb);
  };

  return {
    getAllUsers,
    register
  };
};

export default chatInit;
