import React from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import Logo from '../Logo/Logo';
import ChatRooms from '../Chatrooms/ChatroomList';

const Main = ({ user }) => (
  <>
    <Logo user={user} />
    <Link to='/users'>{user ? user.name : 'Who are you ?'} </Link>
    <br />
    {user && <ChatRooms />}

    <Switch>
      <Route to='/chatroom'/>
    </Switch>
  </>
);

export default Main;
