import io from 'socket.io-client';

const chatInit = () => {
  const socket = io.connect('http://localhost:8001');

  const chatConnectionHandler = cb => {
    socket.on('message', cb);
  };

  const chatDisconectHandler = () => {
    socket.off('message');
  };

  const sendMessage = (chatRoomName, message,  cd) => {
    socket.emit('message', { chatRoomName, message }, cd);
  };

  const getAllUsers = cb => {
    socket.emit('allUsers', cb);
  };

  const register = (userName, cb) => {
    socket.emit('register', userName, cb);
  };

  const getAllChatrooms = cb => {
    socket.emit('allChatRooms', cb);
  };

  return {
    getAllUsers,
    register,
    getAllChatrooms,
    chatConnectionHandler,
    chatDisconectHandler,
    sendMessage
  };
};

export default chatInit;
