import styled from 'styled-components';

const Backdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(0, 0, 0, 0.4);
  overflow: hidden;
`;

const ReturnBtn = styled.div`
  background-color: yellow;
  width: 200px;
  max-height: 50px;
  position: absolute;
  top: 0;
  left: 0;
`;

// const  = styled.div`

// `;

const ModalWindow = styled.div`
  width: 900px;
  height: 400px;
  padding-right: 17px; 
  display: flex;
  justify-content: center;
  overflow-y: scroll;
  box-sizing: content-box;
  background-color: rgba(0, 0, 0, 0.4);
  color: white;
  text-align: justify;
  position: relative;
`;
export { Backdrop, ModalWindow, ReturnBtn };
