import React, { Component } from 'react';
import { ChatWrapper, MessageArea } from './styles';

import chatInit from '../../services/socket';

class Chat extends Component {
  state = {
    chatHistory: [],
    message: '',
    socketClient: chatInit()
  };

  componentDidMount() {
    // this.setState({
    //   socketClient: chatInit()
    // });

    // console.log(this.state.socketClient);
    // this.state.socketClient.chatConnectionHandler(this.updateChatHistory);
  }

  updateChatHistory = msg => {
    this.setState({
      chatHistory: this.state.chatHistory.concat(msg)
    });
  };

  // onMessageReceived = () => {
  //   console.log('message', this.state.message);
  //   this.setState(prev => ({
  //     chatHistory: [...prev.chatHistory, this.state.message],
  //     message: ''
  //   }));
  // };

  onMessageSend = () => {
    const { socketClient, message } = this.state;
    const {
      match: { params },
      user
    } = this.props;

    return socketClient.sendMessage({ roomname: params.roomname, message, userId: user.id }, () => null);
    this.setState({})
  };

  onMessageChange = e => this.setState({ message: e.target.value });

  render() {
    const { history } = this.props;
    const { message, chatHistory } = this.state;

    return (
      <ChatWrapper>
        <h1>dialog</h1>
        <p onClick={() => history.push('/')}>back</p>
        <ul>
          {chatHistory.map(message => (
            <li key={message}>{message}</li>
          ))}
        </ul>
        <MessageArea rows='1' onChange={this.onMessageChange} value={message} />
        <button type='button' onClick={this.onMessageSend}>
          send message
        </button>
      </ChatWrapper>
    );
  }
}

export default Chat;
