import React from 'react';
import UsersView from './UserView';
import styled from 'styled-components';

const Backdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWindow = styled.div`
  width: 900px;
  max-height: 400px;
  background-color: blue;
  padding: 16px;
  overflow: auto;
  display: flex;
  justify-content: center;
  position: relative;
`;

const ReturnBtn = styled.div`
  background-color: yellow;
  width: 200px;
  max-height: 50px;
  position: absolute;
  top: 0;
  left: 0;
`;

const UserList = ({ allUsers, handleUserSelection, history }) => (
  <Backdrop>
    <ModalWindow>
      {/* <ReturnBtn onClick={history.push('/')}>Go Back</ReturnBtn> */}

      <ul>
        {allUsers &&
          allUsers.map(user => (
            <li key={user.name}>
              <UsersView key={user.id} user={user} onClick={() => handleUserSelection(user.name)} />
            </li>
          ))}
      </ul>
    </ModalWindow>
  </Backdrop>
);

export default UserList;
