import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import Logo from './Logo/Logo';
import Users from './Users/UserContainer';
import chatInit from '../services/socket';
import './App.css';

class App extends Component {
  state = {
    user: null,
    users: []
  };

  componentDidMount() {
    const { getAllUsers } = chatInit();

    getAllUsers((err, users) => {
      this.setState({ users: users });
    });
  }

  setCurrentUser = id => {

    const { users } = this.state;
    this.setState({
      user: users.filter(user => user.id === id)
    });
  };

  render() {
    const { user, users } = this.state;
    return (
      <Switch>
        <Route path='/' exact render={() => <Logo user={user} />} />
        <Route path='/users' render={() => <Users users={users} setCurrentUser={this.setCurrentUser} />} />
      </Switch>
    );
  }
}

export default App;
