import styled from 'styled-components';

const Backdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWindowInner = styled.div`
  width: 900px;
  max-height: 400px;
  overflow: auto;
`;

const ModalWindow = styled.div`
  width: 900px;
  max-height: 400px;
  background-color: blue;
  display: flex;
  justify-content: center;
  position: relative;

  overflow: hidden;
`;

const ReturnBtn = styled.div`
  background-color: yellow;
  width: 200px;
  max-height: 50px;
  position: absolute;
  top: 0;
  left: 0;
`;

const hiddenScrollbar = styled.div`
  background-color: black;
  border: 2px solid #666;
  color: white;
  overflow: hidden;
  text-align: justify;
`;

const Inner = styled.div`
  height: 200px;
  overflow: auto;
  margin: 15px -300px 15px 15px;
  padding-right: 300px; /* Samakan dengan besar margin negatif */
`;

export { Backdrop, ModalWindow, ModalWindowInner, ReturnBtn, hiddenScrollbar,  };
