import React, { Component } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';
import Logo from './Logo/Logo';
import Users from './Users/UserContainer';
import chatInit from '../services/socket';
import './App.css';

class App extends Component {
  state = {
    user: null,
    isRegistrationInProcess: false,
    client: chatInit()
  };

  register = userName => {
    const { register } = this.state.client;

    this.setState({
      isRegistrationInProcess: true
    });

    register(userName, (err, user) => {
      if (err) return this.setState({ user: null });

      this.setState({
        isRegistrationInProcess: false,
        user
      });
    });
  };

  renderUserListOrRedirect = userList => {
    if (this.state.users) {
      return <Redirect to='/' />;
    }

    return this.state.isRegistrationInProcess ? <p>LOADING</p> : userList();
  };

  render() {
    const { user,  } = this.state;
    return (
      <Switch>
        <Route path='/' exact render={() => <Logo user={user} />} />
        <Route
          path='/users'
          render={props => {
            const home = () => props.history.push('/');
            return this.renderUserListOrRedirect(() => <Users />);
          }}
        />
      </Switch>
    );
  }
}

export default App;
