import styled from 'styled-components';

const Backdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWindowInner = stled.div`
width: 900px;
max-height: 400px;
 overflow-y: scroll; 

`;

const ModalWindow = styled.div`
  width: 900px;
  max-height: 400px;
  background-color: blue;
  display: flex;
  justify-content:   center;
  position: relative;

  overflow: hidden;
`;

const ReturnBtn = styled.div`
  background-color: yellow;
  width: 200px;
  max-height: 50px;
  position: absolute;
  top: 0;
  left: 0;
`;

export { Backdrop, ModalWindow, ModalWindowInner, ReturnBtn };
