import React, { Component } from 'react';
import UserList from './UserList';

class UserContainer extends Component {
  state = {
    allUsers: []
  };

  componentDidMount() {
    const { getAllUsers } = this.props;

    getAllUsers((err, allUsers) => {
      this.setState({
        allUsers
      });
    });
  }

  handleUserSelection = userName => {
    const { history, register } = this.props;

    register(userName);
    history.push('/');
  };

  render() {
    const {} = this.state;
    const { history, allUsers } = this.props;
    return (
      <div>
        <UserList allUsers={allUsers} handleUserSelection={this.handleUserSelection} history={history} />
      </div>
    );
  }
}

export default UserContainer;
