import React, { Component } from 'react';
import { ChatWrapper, MessageArea } from './styles';

import chatInit from '../../services/socket';

class Chat extends Component {
  state = {
    chatHistory: [],
    message: ''
  };

  componentDidMount() {
    const { chatConnectionHandler } = chatInit();
    chatConnectionHandler(this.onMessageReceived);
  }

  onMessageReceived = () => {
    console.log('message', this.state.message);
    this.setState(prev => ({ chatHistory: [...prev.chatHistory, this.state.message] }));
  };

  onMessageChange = e => this.setState({ message: e.target.value });

  render() {
    const { history } = this.props;
    const { message, chatHistory } = this.state;

    return (
      <ChatWrapper>
        <h1>dialog</h1>
        <p onClick={() => history.push('/')}>back</p>
        <ul>{}</ul>
        <MessageArea rows='1' onChange={this.onMessageChange} value={message} />
        <button type='button' onClick={this.onMessageReceived}>
          send message
        </button>
      </ChatWrapper>
    );
  }
}

export default Chat;
