import React from 'react';

const ChatroomList = ({ chatrooms }) => {
  return (
    <ul>
      {chatrooms.map(room => (
        <li key={room.name}>
          <Link to={`/chatroom/${room.name}`}>
            <img src={room.image} alt='room' />
            <p>{room.name}</p>
          </Link>
        </li>
      ))}
    </ul>
  );
};

export default ChatroomList;
